mapper.select:
select count(*) from t_trade


		and create_time >= #{start_time}


		and create_time <= DATE_FORMAT(DATE_ADD(#{end_time},INTERVAL 1 DAY),'%Y-%m-%d');

select t.out_trade_no out_trade_no,trade_no trade_no,total_amount total_amount, body body,user_id user_id,create_time create_time,buy_count buy_count,pt.pay_type_name pay_type_name, u.username user_name from t_trade t LEFT JOIN t_pay_type pt ON t.pay_type = pt.pay_type_id LEFT JOIN t_user6 u ON t.user_id = u.userId
and t.create_time >= #{trade.start_time}
and create_time <= DATE_FORMAT(DATE_ADD(#{trade.end_time},INTERVAL 1 DAY),'%Y-%m-%d')
order by create_time LIMIT #{s},#{r}
